mapper.insert:
INSERT INTO tbl_moneylog
(
	mno
  , paydate
  , paymethod
  , amount
  , item
  , ownerid
  , category
)
VALUES
(
	SEQ_MOENYLOG.nextval
  , #{payDate}
  , #{payMethod}
  , #{amount}
  , #{item}
  , #{ownerId}
  , #{category}
)

mapper.update:
UPDATE tbl_moneylog
   SET paydate	= #{payDate}
	 , payMethod= #{payMethod}
	 , item		= #{item}
	 , category	= #{category}
 WHERE mno = #{mno}
